=== 父类文件内容 ===
package test;
import java.awt.Graphics2D;
import java.awt.geom.AffineTransform;

import java.awt.Shape;

public abstract class BaseShape {

    protected double x = 0;
    protected double y = 0;

    public void move(double dx, double dy) {
        this.x += dx;
        this.y += dy;
    }

    public void setLocation(double x, double y) {
        this.x = x;
        this.y = y;
    }

    protected AffineTransform transform;

    public void render(java.awt.Graphics2D g2d) {
        Shape shape = getTransformedShape();
        g2d.draw(shape);
        if (transform != null) {
            g2d.fill(shape);
        }
    }

    public abstract Shape getTransformedShape();
}


=== EllipseShape文件内容 ===
package test;

import java.awt.Shape;
import java.awt.geom.Ellipse2D;
import java.awt.geom.AffineTransform;

public class EllipseShape extends BaseShape {

    private Ellipse2D.Double ellipse = new Ellipse2D.Double();
    private transient Shape cachedTransformedShape;

    @Override
    public Shape getTransformedShape() {
        if (cachedTransformedShape == null) {
            if (transform == null) {
                cachedTransformedShape = ellipse;
            } else {
                cachedTransformedShape = transform.createTransformedShape(ellipse);
            }
        }
        return cachedTransformedShape;
    }

    public void setTransform(AffineTransform transform) {
        this.transform = transform;
        cachedTransformedShape = null;
    }

    public void setFrame(double x, double y, double w, double h) {
        ellipse.setFrame(x, y, w, h);
        cachedTransformedShape = null;
    }
}

EllipseShape中的@Override注解格式: @Override

=== RectShape文件内容 ===
package test;

import java.awt.Shape;
import java.awt.geom.Rectangle2D;
import java.awt.geom.AffineTransform;

public class RectShape extends BaseShape {

    private Rectangle2D.Double rect = new Rectangle2D.Double();
    private transient Shape cachedTransformedShape;
    private AffineTransform transform;

    @Override
    public void render(java.awt.Graphics2D g2d) {
        Shape shape = getTransformedShape();
        g2d.draw(shape);
        if (transform != null) {
            g2d.fill(shape);
        }
    }

    public Shape getTransformedShape() {
        if (cachedTransformedShape == null) {
            if (transform == null) {
                cachedTransformedShape = rect;
            } else {
                cachedTransformedShape = transform.createTransformedShape(rect);
            }
        }
        return cachedTransformedShape;
    }

    public void setTransform(AffineTransform transform) {
        this.transform = transform;
        cachedTransformedShape = null;
    }

    public void setSize(double width, double height) {
        rect.setFrame(x, y, width, height);
        cachedTransformedShape = null;
    }
}

